import React, { useContext } from "react";
import Card from "./Card"
import CharacterContext from '../utils/CharacterContext'
import CheckBox from './CheckBox'




const Home= () => {
    const Context= useContext(CharacterContext);
    const { state: Characters} = Context
    const { addCharacter} = Context.bunchaFunctions
    const {checkBoxStatus} = Context.bunchaFunctions
   //Sort the funtion Aphabetically
    
 
  
    const compare = (a, b) => {
        // Use toUpperCase() to ignore character casing
        const nameA = a.name.toUpperCase();
        const nameB = b.name.toUpperCase();
        
      
        let comparison = 0;
        if (nameA > nameB) {
          comparison = 1;
        } else if (nameA < nameB) {
          comparison = -1;
        }
        return comparison;
      }
    let aphabetCharacters = Characters.sort(compare)

    
  
  
    
    
    
return(
    <div>
    <CheckBox />
    <div className="App" onClick={addCharacter}>
    {
        
    Characters.map((character) =>  <Card key = {character.id} character = {character}/>)
    }
   </div>
   </div>

  );
}
 
export default Home;
