import useSignUpForm from '../utils/CustomHooks';
import CharacterContext from '../utils/CharacterContext'

function Add() {
  const Context = useContext(CharacterContext)
  console.log(Context)
    const signup = () => {
        alert(`User Created!
               Name: ${inputs.name} ${inputs.powerLevel}
               Email: ${inputs.age}`);
      }
    
    const {inputs, handleInputChange, handleSubmit} = useSignUpForm(signup);

return(
  <form onSubmit={handleSubmit}>
  <label>Name</label>
  <input  name="name" type="text" onChange={handleInputChange} value="name" required/>
  <label>powerLevel</label>
  <input  name="powerLevel" type="text" onChange={handleInputChange} value="powerLevel" required/>
  <label>age</label>
  <input  name="age" type="text" onChange={handleInputChange} value="age" required/>
  <button type="submit">Add</button>
</form>
  );
}
 
export default Add;
